import logo from './logo.svg';
import './App.css';

function App() {

  let [글제목, 글제목변경] = useState(['State 사용하는 법', 'State를 사용하는 법2', 'State를 사용하는 법3']);
  let [날짜, 날짜변경] = useState(['2월 22일']);

  let posts = 'React 정보'
  let lineSetting = { borderWidth: '2px', borderColor: 'black', marginTop: '30px' }

  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 포트폴리오</div>
      </div>

      <div className='list'>
        <h3> {posts} </h3>
        <p>2025년 2월 22일 발행</p>
      </div>

      <div className='detail'>
        <h3> {글제목[0]} <span>카운트</span> 0 </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

      <div className='detail'>
        <h3> {글제목[1]} </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

      <div className='detail'>
        <h3> {글제목[2]} </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

    </div>
  );
}

export default App;
