import React, { useState } from 'react';
import KakaoLogin from 'react-kakao-login';
import logo from './logo.svg';
import './App.css';

function App() {

  let [글제목, 글제목변경] = useState(['State 사용하는 법', 'State를 사용하는 법2', 'State를 사용하는 법3']);
  let [날짜, 날짜변경] = useState(['2월 22일']);
  let [count, countChange] = useState(0);

  let posts = 'React 정보'
  let lineSetting = { borderWidth: '2px', borderColor: 'black', marginTop: '30px' }

  function ChangeName() {
    var newArray = [...글제목]
    newArray[1] = 'onClick을 사용하는 법'
    글제목변경(newArray)
  }

  function SocialKakao() {
    const kakaoClientId = 'JavaScript KEY'
    const kakaoOnSuccess = async (data)=>{
      console.log(data)
      const idToken = data.response.access_token // 엑세스 토큰 백엔드로 전달
    }
    const kakaoOnFailure = (error) => {
      console.log(error);
    }
    return (
      <>
        <KakaoLogin
          token = {kakaoClientId}
          onSuccess = {kakaoOnSuccess}
          onFail = {kakaoOnFailure} 
        />
      </>
    )
  }

  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 포트폴리오</div>
      </div>

      <div className='list'>
        <h3> {posts} </h3>
        <p>2025년 2월 22일 발행</p>
        <button onClick={() => { ChangeName() }}>바꾸기 버튼</button>
      </div>

      <div className='detail'>
        <h3> {글제목[0]} <span onClick={() => { countChange(count + 1) }}>👍</span> {count} </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

      <div className='detail'>
        <h3> {글제목[1]} </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

      <div className='detail'>
        <h3> {글제목[2]} </h3>
        <p>{날짜[0]}</p>
        <hr style={ lineSetting }/>
      </div>

      <Modal>

      </Modal>

      <div className='kakao'>
        <button 
          onClick={() => { SocialKakao(); console.log(1); }}
          style={{ backgroundColor: 'yellow' }}
        >
          카카오로 로그인하기</button>
      </div>

    </div>
  );
}

function Modal() {
  return (
    <div className='modal'>
      <h2>제목</h2>
        <p>날짜</p>
        <p>상세내용</p>
    </div>
  )
}

export default App;
